import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Avatar from '@material-ui/core/Avatar';
import MyLocationIcon from '@material-ui/icons/MyLocation';
import EventIcon from '@material-ui/icons/Event';



const useStyles = makeStyles(theme => ({
   
    paper: {
      height: '100%',
      backgroundColor: theme.palette.background.paper,
      boxShadow: theme.shadows[5],
      padding: theme.spacing(0, 0, 0),
      outline: 'none',
      margin: '1%',
      display:"block",
      justifyContent: "center",
      alignItems: "center",
      boxShadow: "0 16px 19px rgba(0,0,0,0.2), 0 15px 15px rgba(0,0,0,0.2)",
      '&:hover': {
        boxShadow: "0 2px 4px rgba(0,0,0,0.25), 0 2px 2px rgba(0,0,0,0.22)"
      },
      [theme.breakpoints.down('md')]: {
        width: '100%',
        height: '100%',
      },
      [theme.breakpoints.down('sm')]: {
        padding: theme.spacing(0, 0, 0),
        width: '100%',
        height: '100%',
      },
    },

    profileImage:{

        width:'80%',
        height:'80%',
        margin: "5px auto"
    }
  
  
  }))


function ProfileImage(props) {
    const classes = useStyles();




  return (

    <Card className={classes.paper} style={{ border: "black", minWidth: "350px", minHeight: "350px", maxWidth: "350px" }}>  

        <Avatar className = {classes.profileImage}  alt={`${props.user.login}`} src={props.user.avatar_url} />

        <p style={{color:'black'}}>{props.username}</p>

        <div className={classes.subSection}>
        <MyLocationIcon  style={{ borderRadius: "50%" }} className={classes.margin} />
        <p>{props.user.location}</p>

        </div>
       
        <div>
        <EventIcon  style={{ borderRadius: "50%" }} className={classes.margin} />
        <p>{new Date(props.user.created_at).toLocaleDateString('en-US', {
                month: 'long',
                day: 'numeric',
                year: 'numeric',
              })}</p>
            </div>
        

    </Card>

  );
}

export default ProfileImage;
