import React ,{useEffect,useState} from 'react';
import '../App.css';
import {gsap} from 'gsap';


const randomListOfNums =(n)=>
{
    const nums = [];

    for( let i =0; i<n; i++)
    {
        let randomNum = Math.floor(Math.random() * 30) + 1;

        nums.push({num : `${randomNum}`,ref:`num${randomNum}` });

    }

    return nums;   

}


function Insertion() {

