                <div 
                ref={obj.ref}
                style={{height:`${obj.num}px`, backgroundColor:"red",margin:"5px",width:"80%"}}
                >
                    
                   <p>{obj.num}</p>

                </div>
            )

         })}

        </section>

    </div>
  );
}

export default Insertion;
