import React ,{useEffect,useState} from 'react';
import '../App.css';
import {gsap} from 'gsap';


const randomListOfNums =(n)=>
{
    const nums = [];

    for( let i =0; i<n; i++)
    {
        let randomNum = Math.floor(Math.random() * 30) + 1;

        nums.push(randomNum);

    }

    return nums;   

}


function Insertion() {

    const [values, setValues] = useState(randomListOfNums(20))

    // const index = React.createRef();

  return (
    <div className="insertion" >

           <p>insertion</p>

        <section style={{display:"flex", flexDirection:"row",width:"1200px"}}>

         {values.map((elem,index)=>{

            console.log(index)

            return (

                <div 
                style={{height:`${elem}px`, backgroundColor:"red",margin:"5px",width:"80%"}}
                >
                    
                   <p>{elem}</p>

                </div>
            )


         })}

        </section>

    </div>
  );
}

export default Insertion;
