    .to(revealsRefMobile.current[0], 0.9, {y:  (rect2.bottom-rect.bottom),delay: 0.5})
    .to(revealsRefMobile.current[10], 0.9, {y: -(rect2.bottom-rect.bottom) ,delay: 0.5})
    
    .to(revealsRefMobile.current[0], 0.9, {x: -(rect2.bottom-rect2.top+ revealsRefMobile.current[10]),delay: 0.5,backgroundColor:"white"})
